import React, { useState, useEffect } from 'react';
import axios from 'axios';
import DogCard from '../components/DogCard';
import Pagination from '../components/Pagination';
import { useAuth } from '../context/AuthContext';

const Search = () => {
    const [dogs, setDogs] = useState([]);
    const [breeds, setBreeds] = useState([]);
    const [selectedBreed, setSelectedBreed] = useState('');
    const [page, setPage] = useState(0);
    const [sortOrder, setSortOrder] = useState('asc');
    const [hasNext, setHasNext] = useState(false);

    const { favorites, setFavorites } = useAuth(); // Get favorites and setter from context

    useEffect(() => {
        const fetchBreeds = async () => {
            try {
                const response = await axios.get(
                    'https://frontend-take-home-service.fetch.com/dogs/breeds',
                    { withCredentials: true }
                );
                setBreeds(response.data || []);
            } catch (error) {
                console.error('Error fetching breeds:', error);
            }
        };
        fetchBreeds();
    }, []);

    useEffect(() => {
        const fetchDogs = async () => {
            try {
                // Fetch dog IDs from the search endpoint
                const searchResponse = await axios.get(
                    'https://frontend-take-home-service.fetch.com/dogs/search',
                    {
                        params: {
                            breeds: selectedBreed ? [selectedBreed] : [],
                            size: 10,
                            from: page * 10,
                            sort: `breed:${sortOrder}`,
                        },
                        withCredentials: true,
                    }
                );

                const dogIds = searchResponse.data.resultIds || [];
                setHasNext(!!searchResponse.data.next);

                if (dogIds.length > 0) {
                    // Fetch full dog details using the IDs
                    const dogsResponse = await axios.post(
                        'https://frontend-take-home-service.fetch.com/dogs',
                        dogIds,
                        { withCredentials: true }
                    );
                    setDogs(dogsResponse.data || []);
                } else {
                    setDogs([]);
                }
            } catch (error) {
                console.error('Error fetching dogs:', error);
            }
        };
        fetchDogs();
    }, [selectedBreed, page, sortOrder]);

    const toggleFavorite = (dog) => {
        setFavorites((prevFavorites) =>
            prevFavorites.some((fav) => fav.id === dog.id)
                ? prevFavorites.filter((fav) => fav.id !== dog.id)
                : [...prevFavorites, dog]
        );
    };

    const generateMatch = async () => {
        try {
            const dogIds = favorites.map((dog) => dog.id);
            console.log('Favorited Dog IDs:', dogIds);

            const response = await axios.post(
                'https://frontend-take-home-service.fetch.com/dogs/match',
                dogIds,
                { withCredentials: true }
            );

            console.log('Match API Response:', response.data);

            if (response.data.match) {
                alert(`Your match is: ${response.data.match}`);
            } else {
                alert('No match found. Please favorite more dogs and try again.');
            }
        } catch (error) {
            console.error('Error generating match:', error);
            alert('Failed to generate a match. Please try again.');
        }
    };

    return (
        <div>
            <h1>Search Dogs</h1>

            {/* Breed Selector */}
            <select onChange={(e) => setSelectedBreed(e.target.value)}>
                <option value="">All Breeds</option>
                {breeds.map((breed) => (
                    <option key={breed} value={breed}>
                        {breed}
                    </option>
                ))}
            </select>

            {/* Sort Order Toggle */}
            <button onClick={() => setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc')}>
                Sort: {sortOrder === 'asc' ? 'Ascending' : 'Descending'}
            </button>

            {/* Dog Cards */}
            <div>
                {dogs.map((dog) => (
                    <DogCard
                        key={dog.id}
                        dog={dog}
                        isFavorite={favorites.some((fav) => fav.id === dog.id)}
                        toggleFavorite={toggleFavorite}
                    />
                ))}
            </div>

            {/* Pagination */}
            <Pagination page={page} setPage={setPage} hasNext={hasNext} />

            {/* Generate Match Button */}
            <button onClick={generateMatch} disabled={favorites.length === 0}>
                Generate Match
            </button>
        </div>
    );
};

export default Search;
