import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { useAuth } from '../context/AuthContext';

const Login = () => {
    console.log('Login component rendered');

    // State variables for name, email, loading, and errors
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [loading, setLoading] = useState(false); // For showing a loading indicator
    const [error, setError] = useState(''); // For displaying errors
    const { setUser } = useAuth();
    const navigate = useNavigate();

    // Input validation function
    const validateInputs = () => {
        if (!name || !email) {
            setError('Both name and email are required.');
            return false;
        }
        const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
        if (!emailRegex.test(email)) {
            setError('Invalid email format.');
            return false;
        }
        setError(''); // Clear any previous errors
        return true;
    };

    // Handle login function
    const handleLogin = async () => {
        if (!validateInputs()) return;

        setLoading(true); // Start loading
        try {
            // Make the login API request
            await axios.post(
                'https://frontend-take-home-service.fetch.com/auth/login',
                { name, email },
                { withCredentials: true }
            );

            setUser({ name, email }); // Save user info in context
            navigate('/search'); // Navigate to the search page
        } catch (error) {
            console.error('Login failed:', error);
            setError('Login failed. Please check your credentials and try again.');
        } finally {
            setLoading(false); // Stop loading
        }
    };

    return (
        <div style={styles.container}>
            <h1>Login</h1>
            {error && <p style={styles.error}>{error}</p>} {/* Display errors */}
            <input
                type="text"
                placeholder="Enter your name"
                value={name}
                onChange={(e) => setName(e.target.value)}
                style={styles.input}
            />
            <input
                type="email"
                placeholder="Enter your email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                style={styles.input}
            />
            <button
                onClick={handleLogin}
                disabled={loading}
                style={loading ? styles.buttonDisabled : styles.button}
            >
                {loading ? 'Logging in...' : 'Login'}
            </button>
        </div>
    );
};

// Inline styles for the component
const styles = {
    container: {
        maxWidth: '400px',
        margin: '50px auto',
        padding: '20px',
        textAlign: 'center',
        backgroundColor: '#f8f9fa',
        borderRadius: '8px',
        boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
    },
    input: {
        width: '90%',
        padding: '10px',
        margin: '10px 0',
        border: '1px solid #ccc',
        borderRadius: '4px',
    },
    button: {
        width: '90%',
        padding: '10px',
        backgroundColor: '#007bff',
        color: '#fff',
        border: 'none',
        borderRadius: '4px',
        cursor: 'pointer',
    },
    buttonDisabled: {
        width: '90%',
        padding: '10px',
        backgroundColor: '#6c757d',
        color: '#fff',
        border: 'none',
        borderRadius: '4px',
        cursor: 'not-allowed',
    },
    error: {
        color: 'red',
        marginBottom: '10px',
    },
};

export default Login;
