import React, { useState, useEffect } from 'react';
import axios from 'axios';
import DogCard from '../components/DogCard';
import Pagination from '../components/Pagination';
import { useAuth } from '../context/AuthContext';

const Search = () => {
    const [dogs, setDogs] = useState([]);
    const [breeds, setBreeds] = useState([]);
    const [selectedBreed, setSelectedBreed] = useState('');
    const [page, setPage] = useState(0);
    const [sortOrder, setSortOrder] = useState('asc');
    const [hasNext, setHasNext] = useState(false);
    const { favorites, setFavorites } = useAuth();

    useEffect(() => {
        const fetchBreeds = async () => {
            const response = await axios.get('https://frontend-take-home-service.fetch.com/dogs/breeds', { withCredentials: true });
            setBreeds(response.data);
        };
        fetchBreeds();
    }, []);

    useEffect(() => {
        const fetchDogs = async () => {
            const response = await axios.get('https://frontend-take-home-service.fetch.com/dogs/search', {
                params: {
                    breeds: selectedBreed ? [selectedBreed] : [],
                    size: 10,
                    from: page * 10,
                    sort: `breed:${sortOrder}`,
                },
                withCredentials: true,
            });
            setDogs(response.data.resultIds);
            setHasNext(!!response.data.next);
        };
        fetchDogs();
    }, [selectedBreed, page, sortOrder]);

    const toggleFavorite = (dog) => {
        setFavorites((prev) =>
            prev.some((fav) => fav.id === dog.id) ? prev.filter((fav) => fav.id !== dog.id) : [...prev, dog]
        );
    };

    return (
        <div>
            <h1>Search Dogs</h1>
            <select onChange={(e) => setSelectedBreed(e.target.value)}>
                <option value="">All Breeds</option>
                {breeds.map((breed) => (
                    <option key={breed} value={breed}>{breed}</option>
                ))}
            </select>
            <button onClick={() => setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc')}>
                Sort: {sortOrder === 'asc' ? 'Ascending' : 'Descending'}
            </button>
            <div>
                {dogs.map((dog) => (
                    <DogCard
                        key={dog.id}
                        dog={dog}
                        isFavorite={favorites.some((fav) => fav.id === dog.id)}
                        toggleFavorite={toggleFavorite}
                    />
                ))}
            </div>
            <Pagination page={page} setPage={setPage} hasNext={hasNext} />
        </div>
    );
};

export default Search;
