import React, { useState, useEffect } from 'react';
import axios from 'axios';
import DogCard from '../components/DogCard';
import Pagination from '../components/Pagination';
import { useAuth } from '../context/AuthContext';

const Search = () => {
    const [dogs, setDogs] = useState([]);
    const [breeds, setBreeds] = useState([]);
    const [selectedBreed, setSelectedBreed] = useState('');
    const [page, setPage] = useState(0);
    const [sortOrder, setSortOrder] = useState('asc');
    const [hasNext, setHasNext] = useState(false);

    const { favorites, setFavorites } = useAuth(); // Access favorites and setFavorites from context

    useEffect(() => {
        const fetchBreeds = async () => {
            try {
                const response = await axios.get(
                    'https://frontend-take-home-service.fetch.com/dogs/breeds',
                    { withCredentials: true }
                );
                setBreeds(response.data || []);
            } catch (error) {
                console.error('Error fetching breeds:', error);
            }
        };
        fetchBreeds();
    }, []);

    useEffect(() => {
        const fetchDogs = async () => {
            try {
                const response = await axios.get(
                    'https://frontend-take-home-service.fetch.com/dogs/search',
                    {
                        params: {
                            breeds: selectedBreed ? [selectedBreed] : [],
                            size: 10,
                            from: page * 10,
                            sort: `breed:${sortOrder}`,
                        },
                        withCredentials: true,
                    }
                );
                setDogs(response.data.resultIds || []);
                setHasNext(!!response.data.next);
            } catch (error) {
                console.error('Error fetching dogs:', error);
            }
        };
        fetchDogs();
    }, [selectedBreed, page, sortOrder]);

    const toggleFavorite = (dog) => {
        setFavorites((prevFavorites) =>
            prevFavorites.some((fav) => fav.id === dog.id)
                ? prevFavorites.filter((fav) => fav.id !== dog.id)
                : [...prevFavorites, dog]
        );
    };

    return (
        <div>
            <h1>Search Dogs</h1>

            {/* Breed Selector */}
            <select onChange={(e) => setSelectedBreed(e.target.value)}>
                <option value="">All Breeds</option>
                {breeds.map((breed) => (
                    <option key={breed} value={breed}>
                        {breed}
                    </option>
                ))}
            </select>

            {/* Sort Order Toggle */}
            <button onClick={() => setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc')}>
                Sort: {sortOrder === 'asc' ? 'Ascending' : 'Descending'}
            </button>

            {/* Dog Cards */}
            <div>
                {dogs.map((dog) => (
                    <DogCard
                        key={dog.id}
                        dog={dog}
                        isFavorite={favorites.some((fav) => fav.id === dog.id)}
                        toggleFavorite={toggleFavorite} // Pass toggleFavorite correctly
                    />
                ))}
            </div>

            {/* Pagination */}
            <Pagination page={page} setPage={setPage} hasNext={hasNext} />
        </div>
    );
};

export default Search;
